mapper.select:
select




from chat_message



			and message_id = #{pojo.messageId}


			and session_id = #{pojo.sessionId}


			and send_user_id = #{pojo.sendUserId}


			and contact_id = #{pojo.contactId}


			and send_time = #{pojo.sendTime}




	limit #{page.pageSize} offset #{page.pageNum}


	order by #{sort.index} #{sort.order}

mapper.delete:
delete from chat_message


		and message_id = #{pojo.messageId}


		and session_id = #{pojo.sessionId}


