from group_info



			and group_id = #{pojo.groupId}


			and group_name = #{pojo.groupName}


			and group_owner_id = #{pojo.groupOwnerId}


			and status = #{pojo.status}




	limit #{page.pageSize} offset #{page.pageNum}


	order by #{sort.index} #{sort.order}

